# bsc = "https://data-seed-prebsc-1-s1.binance.org:8545/"
web3 = Web3(Web3.HTTPProvider(bsc))
print('Binance Chain connected:' + str(web3.isConnected()))
# set account & contract addresses
main_address = 'Your account public key'
contract_address = web3.toChecksumAddress('multisend contract address') #0x49ad5ae0b902360029b222cb6516521afa8e1141
