contract = web3.eth.contract(address = contract_address, abi = abi)
# input receiver's addresses
to = config.to
ind = 0
for i in to:
    send = 1000
    amount = web3.toWei(send, 'ether')
    nonce = web3.eth.getTransactionCount(main_address)
    
    token_tx = contract.functions.transfer(web3.toChecksumAddress(i), amount).buildTransaction({
        'chainId': 56, 'gas': 100000, 'gasPrice': web3.toWei('50', 'gwei'), 'nonce': nonce
    })
    sign_txn = web3.eth.account.signTransaction(token_tx, private_key=config.private)
    tx_hash = web3.eth.sendRawTransaction(sign_txn.rawTransaction)
    trans = web3.toHex(tx_hash)
    balance = web3.eth.get_balance(main_address)
    print(str(ind) + ' =====> ' + str(trans) + ' (current balance: ' + str(web3.fromWei(balance, 'ether')) + ')')
    time.sleep(10)
    ind +=1